query GetArticleDisclosures($_v0_uuid: String) {
  article(uuid: $_v0_uuid) {
    companies {
      uuid
      __typename
    }
    companyCompliances {
      uuid
      name
      analysts {
        firstName
        lastName
        isShareholder
        __typename
      }
      compliance {
        hasActiveRedeyeServices
        hasRedeyeTransactionInPast12Months
        hasRedeyeMarketMaker
        hasRedeyeShareholder
        __typename
      }
      __typename
    }
    __typename
  }
}
