query GetCompanyOverView($_v0_id: String) {
  company(id: $_v0_id) {
    mainStock {
      symbol
      __typename
    }
    name
    description
    currency {
      equity
      report
      __typename
    }
    analystIsShareholder
    analysts {
      firstName
      lastName
      title
      cloudinaryId
      __typename
    }
    update: latestArticles(types: [RESEARCH_UPDATE], limit: 1) {
      url
      publishedAt
      __typename
    }
    initiation: latestArticles(types: [INITIATION_COVERAGE], limit: 1) {
      url
      publishedAt
      __typename
    }
    latestArticles(limit: 10) {
      uuid
      title
      url
      publishedAt
      __typename
    }
    financials {
      enterpriseValue
      netDebt
      pricePerEarnings
      evPerEbit
      evPerSales
      pricePerEarningsToGrowth
      dividendYield
      marketCap
      __typename
    }
    market {
      lastPaidPrice
      priceChange1d
      volume
      volume30dAverage
      nextEarnings
      priceChange1dPrc
      __typename
    }
    companyQuality {
      business {
        score
        __typename
      }
      financials {
        score
        __typename
      }
      people {
        score
        __typename
      }
      __typename
    }
    catalysts {
      title
      text
      negativePotency
      positivePotency
      negativeLikelihood
      positiveLikelihood
      timeframe
      __typename
    }
    fairValueRange {
      baseCase {
        value
        __typename
      }
      bearCase {
        value
        __typename
      }
      bullCase {
        value
        __typename
      }
      __typename
    }
    lastPaidPrice
    notice
    __typename
  }
}
