query GetCompanyRatings($_v0_id: String) {
  company(id: $_v0_id) {
    companyQuality {
      business {
        score
        __typename
      }
      financials {
        score
        __typename
      }
      people {
        score
        __typename
      }
      __typename
    }
    catalysts {
      title
      text
      negativePotency
      positivePotency
      negativeLikelihood
      positiveLikelihood
      timeframe
      __typename
    }
    fairValueRange {
      baseCase {
        value
        __typename
      }
      bearCase {
        value
        __typename
      }
      bullCase {
        value
        __typename
      }
      __typename
    }
    lastPaidPrice
    __typename
  }
}
